mapper.insert:
INSERT INTO board
	 (
	 	ID
	 ,	TITLE
	 ,	CONTENTS
	 ,	FIRST_DATE
	 ,	RENEWAL_DATE
	 )
VALUES
	 (
	 	#{id}
	 ,	#{title}
	 ,	#{contents}
	 ,	NOW()
	 ,	NOW()
	 )

mapper.update:
UPDATE board
   SET TITLE = #{title}
     , CONTENTS = #{contents}
     , RENEWAL_DATE = NOW()
 WHERE 1 = 1
   AND IDX = #{idx}

mapper.delete:
DELETE FROM board
 WHERE 1 = 1
   AND IDX = #{idx}
